#include <iostream>\n\nint main() {\n    int rows = 3;\n    int cols = 4;\n\n    // Allocate memory for an array of pointers to int\n    int** array = new (std::nothrow) int*[rows];\n    if (!array) {\n        std::cerr << "Memory allocation failed!" << std::endl;\n        return EXIT_FAILURE;\n    }\n\n    // Allocate memory for each row\n    for (int i = 0; i < rows; ++i) {\n        array[i] = new (std::nothrow) int[cols];\n        if (!array[i]) {\n            std::cerr << "Memory allocation failed for row " << i << "!" << std::endl;\n            // Clean up previously allocated memory\n            for (int j = 0; j < i; ++j) {\n                delete[] array[j];\n            }\n            delete[] array;\n            return EXIT_FAILURE;\n        }\n    }\n\n    // Use the array here\n    for (int i = 0; i < rows; ++i) {\n        for (int j = 0; j < cols; ++j) {\n            array[i][j] = i * cols + j;\n        }\n    }\n\n    // Print the array\n    for (int i = 0; i < rows; ++i) {\n        for (int j = 0; j < cols; ++j) {\n            std::cout << array[i][j] << " ";\n        }\n        std::cout << std::endl;\n    }\n\n    // Deallocate memory\n    for (int i = 0; i < rows; ++i) {\n        delete[] array[i];\n    }\n    delete[] array;\n\n    return 0;\n}