#include <windows.h>\n#include <iostream>\n\nint main() {\n    if (OpenClipboard(NULL)) {\n        HANDLE hClipMemory = GetClipboardData(CF_TEXT);\n        char* pszText = static_cast<char*>(GlobalLock(hClipMemory));\n        \n        std::cout << "Clipboard text: " << pszText << std::endl;\n        \n        GlobalUnlock(hClipMemory);\n        CloseClipboard();\n    }\n    \n    return 0;\n}