#include <windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hPipe = CreateFile(TEXT("\\\\.\\pipe\\MyNamedPipe"), GENERIC_READ | GENERIC_WRITE, 0,\n                              NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL , NULL);\n\n    if (hPipe == INVALID_HANDLE_VALUE) {\n        std::cout << "CreateFile failed with error code " << GetLastError() << std::endl;\n        return 1;\n    }\n\n    char message[] = {"Message from client"};\n    DWORD bytesWritten;\n\n    BOOL result = WriteFile(hPipe, &message[0], strlen(message) + 1, &bytesWritten, NULL);\n    \n    if (!result) {\n        std::cout << "WriteFile failed with error code " << GetLastError() << std::endl;\n        CloseHandle(hPipe);\n        return 1;\n    }\n\n    std::cout << "[Client] Message sent to server" << std::endl;\n\n    // Read response from the pipe...\n\n    CloseHandle(hPipe);\n\n    return 0;\n}