#include <iostream>\n#include <list>\n\n#define MAX 100\n\nusing namespace std;\n\nint main()\n{\n    list<int> listA;\n    // Create list of 0 to 100\n    for(int i = 0; i <= MAX; i++)\n        listA.push_back(i);\n\n    // Remove even numbers\n    for(list<int>::iterator itr = listA.begin(); itr != listA.end();)\n    {\n        if (*itr % 2 == 0)\n        {\n            cout << *itr << endl;\n            itr = listA.erase(itr);  // erase() returns an iterator to the element following the removed one\n        }\n        else \n        {\n            ++itr;  // only increment if no elements were erased\n        }\n    }\n\n    return 0;\n}