// Example in C\n     #include <dlfcn.h>\n     #include <stdio.h>\n\n     typedef void (*MyFuncType)(void);\n\n     void my_shared_function() {\n         MyFuncType func;\n         char *error;\n\n         // Load the symbol from the main program's symbol table\n         func = (MyFuncType)dlsym(RTLD_DEFAULT, "my_main_program_function");\n         if ((error = dlerror()) != NULL) {  // Check for errors with dlsym()\n             fprintf(stderr, "%s\n", error);\n             return;\n         }\n         (*func)();  // Call the function\n     }