// Pseudo-code example for Unix\nvoid* handleA = dlopen("libA.so", RTLD_LAZY);\nif (!handleA) {\n    // Handle error\n}\n\nvoid* handleB = dlopen("libB.so", RTLD_LAZY);\nif (!handleB) {\n    dlclose(handleA);  // Clean up A before exiting\n    // Handle error\n}\n\n// Use symbols from B\n...\n\ndlclose(handleB);  // Decrement reference count for B\ndlclose(handleA);  // Decrement reference count for A