#include <arm_neon.h> // Include NEON intrinsics if needed\n\n// Function to convert a single float to int16 using FTOSI instruction\nint16_t fcvt_f32_s16(float input) {\n    register int32_t result;\n    __asm__ volatile (\n        "VMOV.F32     %q0, %s0            \n\t"\n        "VCVT.S16.F32 %h0, %q0, #15      \n\t" // Convert float to int16 with rounding using NEON\n        "VMOV         %d0, %q0            \n\t"\n        : "=r"(result)\n        : "w"(input)\n    );\n    return (int16_t)result;\n}\n\n// Example usage in your code where nextFloat is a float value:\nSInt16 nextInt = fcvt_f32_s16(nextFloat * 32768.0f);