// Assume we have a row structure with 4 columns, each potentially allowing NULLs.\nstruct Row {\n    byte[] data; // Actual column data\n    byte nullBitmap; // Null bitmap for the row (e.g., using bits to represent null status of each column)\n}\n\nfunction isNull(row: Row, columnIdx: int) -> bool {\n    return (row.nullBitmap & (1 << columnIdx)) == 0; // Bitwise check if the bit for this column is set\n}\n\n// Function to read data safely considering NULLs\nfunction readData(row: Row, columnIdx: int, defaultVal: any) -> any {\n    return isNull(row, columnIdx) ? defaultVal : row.data[columnIdx];\n}