// my_watchdog.c\n#include <stdio.h>\n#include <pthread.h>\n\nvoid __attribute__((constructor)) watchdog_init() {\n    pthread_t thread_id;\n    if (pthread_create(&thread_id, NULL, watchdog, NULL) != 0) {\n        perror("Failed to create watchdog thread");\n    }\n}\n\nvoid *watchdog(void *arg) {\n    // Implement your monitoring logic here\n    while(1) {\n        sleep(5); // Check every 5 seconds\n        printf("Watchdog is running...\n");\n    }\n}