#define foreach_value(var, container) \\n  for (__typeof__((container).begin()) CONCAT_HELPER(__foreach_it_, __LINE__) = (container).begin(); \\n       CONCAT_HELPER(__foreach_it_, __LINE__) != (container).end(); \\n       ++CONCAT_HELPER(__foreach_it_, __LINE__)) \\n    for (__typeof__((*CONCAT_HELPER(__foreach_it_, __LINE__)).second) var = (*CONCAT_HELPER(__foreach_it_, __LINE__)).second; \\n         false; ) // This ensures the inner loop runs once per iteration\n\n#define CONCAT(x, y) x##y\n#define CONCAT_HELPER(x, y) CONCAT(x, y)