// Dynamic loading example using Windows API\n    #include <windows.h>\n\n    typedef int (*ADD_FUNC)(int, int);\n\n    int main() {\n        HMODULE hLib = LoadLibrary(TEXT("MyMathLib.dll"));\n        if (hLib != NULL) {\n            ADD_FUNC AddFunc = (ADD_FUNC)GetProcAddress(hLib, "Add");\n            if (AddFunc != NULL) {\n                int result = AddFunc(5, 3);\n                printf("The result is %d\n", result);\n            }\n            FreeLibrary(hLib);\n        }\n        return 0;\n    }