// Pseudo-code for reading raw pixel data from a PNG file\nvoid readPNG(FILE *file) {\n    /* Check the PNG signature */\n    checkSignature(file);\n\n    while (!EOF(file)) {\n        uint32_t length = readLength(file);\n        char type[4];\n        fread(type, 1, 4, file);\n        \n        if (strcmp(type, "IDAT") == 0) {\n            // IDAT stores image data - handle DEFLATE decoding here\n            decodeIDATBlock(file, length);\n        }\n    }\n}