// Example in C for writing a simple structure to a binary file\n   #include <stdio.h>\n\n   typedef struct {\n       char name[20];\n       int age;\n       char city[20];\n   } Person;\n\n   int main() {\n       Person person = {"John Doe", 30, "New York"};\n       FILE *file = fopen("person.bin", "wb");\n       if (file != NULL) {\n           fwrite(&person, sizeof(person), 1, file);\n           fclose(file);\n       }\n       return 0;\n   }