// Example of a simple task scheduler in an RTOS might look like:\n#include <stdio.h>\n\ntypedef struct {\n    int priority;\n    void (*taskFunction)(void);\n} Task;\n\nTask tasks[10];\nint taskCount = 0;\n\nvoid scheduleTasks() {\n    // Sort tasks based on their priorities\n    for (int i = 0; i < taskCount - 1; i++) {\n        for (int j = 0; j < taskCount - i - 1; j++) {\n            if (tasks[j].priority > tasks[j+1].priority) {\n                Task temp = tasks[j];\n                tasks[j] = tasks[j + 1];\n                tasks[j + 1] = temp;\n            }\n        }\n    }\n\n    // Execute tasks in priority order\n    for (int i = 0; i < taskCount; i++) {\n        tasks[i].taskFunction();\n    }\n}\n\nvoid addTask(int priority, void (*func)(void)) {\n    tasks[taskCount].priority = priority;\n    tasks[taskCount].taskFunction = func;\n    taskCount++;\n}