// Register map simulation (typically these values would come from an actual hardware interface)\n#define MAX_REGISTERS 1024\nvolatile unsigned int registers[MAX_REGISTERS] = {0};\n\nvoid c_register_write(int addr, unsigned int data) {\n    if (addr >= 0 && addr < MAX_REGISTERS) {\n        registers[addr] = data;\n    }\n}\n\nunsigned int c_register_read(int addr) {\n    if (addr >= 0 && addr < MAX_REGISTERS) {\n        return registers[addr];\n    }\n    return 0; // or handle error\n}