// C example using Porter2 stemmer\n   #include "libstemmer/libstemmer.h"\n   \n   int main() {\n       struct sb_stemmer * z;\n       const char *p, *q;\n       const int ch = 'e';\n       \n       z = sb_stemmer_new("english", 0);\n       p = "parsing";\n       q = sb_stemmer_stem(z, (sb_symmetry_t*)p, strlen(p));\n       \n       printf("%.*s\n", (int)(q-p), p);\n       \n       sb_stemmer_delete(z);\n       return 0;\n   }