// Example of using strncpy_s from the Secure CRT in Visual Studio or similar environments\n#include <string.h>\n#include <stdio.h>\n\nint main() {\n    char buffer[5];\n    const char* source = "Hello";\n\n    // Using strncpy_s instead of strncpy\n    errno_t err = strncpy_s(buffer, sizeof(buffer), source, 4);\n    if (err != 0) {\n        printf("Error: Buffer too small or other error occurred!\n");\n    } else {\n        buffer[4] = '\0'; // Ensure null-termination\n        printf("Copied string: %s\n", buffer);\n    }\n\n    return 0;\n}