// in importer.c\n\n#include <linux/module.h>\n#include <linux/kernel.h>\n\nint (*helper_function_ptr)(void);\n\nstatic int __init my_driver_init(void) {\n    // Get a reference to the symbol\n    helper_function_ptr = symbol_get(helper_function);\n    if (helper_function_ptr == NULL)\n        return -ENOENT;\n\n    // Use the function\n    helper_function_ptr();\n    \n    return 0;\n}\n\nstatic void __exit my_driver_exit(void) {\n    // Release the symbol\n    if (helper_function_ptr != NULL)\n        symbol_put(helper_function);\n\n    printk(KERN_INFO "Driver exited\n");\n}\n\nmodule_init(my_driver_init);\nmodule_exit(my_driver_exit);\n\nMODULE_LICENSE("GPL");