// Simplified Pseudocode for AES Encryption (single block, AES-128)\nvoid aes_encrypt(const byte input[16], const byte key[16], byte output[16]) {\n    // Expand the 128-bit key into round keys\n    byte expanded_key[17 * 16];\n    expand_key(key, expanded_key);\n\n    // Initial addroundkey\n    for (int i = 0; i < 16; i++) {\n        state[i] = input[i] ^ expanded_key[i];\n    }\n\n    // Main Rounds\n    for (int round = 1; round <= 10; round++) {\n        for (int i = 0; i < 16; i++) {\n            state[i] = s_box[state[i]]; // SubBytes\n        }\n        shift_rows(state);             // ShiftRows\n        if (round != 10) mix_columns(state); // MixColumns in all but the last round\n        add_round_key(state, expanded_key + round * 16); // AddRoundKey\n    }\n\n    // Copy output into result\n    for (int i = 0; i < 16; i++) {\n        output[i] = state[i];\n    }\n}