// Assume these functions are already defined elsewhere\nint eval_plus(node_t* node);\nint eval_minus(node_t* node);\n\nint compile_and_execute(node_t* node) {\n    switch(node->type) {\n        case PLUS:\n            return eval_plus(node);\n        case MINUS:\n            return eval_minus(node);\n        // Add more cases as necessary for other operations\n    }\n}\n\nint eval_plus(node_t* node) {\n    // Directly execute the operation without calling interpret again\n    return compile_and_execute(node->children[0]) + compile_and_execute(node->children[1]);\n}\n\nint eval_minus(node_t* node) {\n    // Directly execute the operation without calling interpret again\n    return compile_and_execute(node->children[0]) - compile_and_execute(node->children[1]);\n}