// Example: Simple MPI program that sums an array in parallel\n#include <mpi.h>\n#include <stdio.h>\n\nint main(int argc, char** argv) {\n    int n = 100;\n    int data[n];\n    int partial_sum = 0;\n    int total_sum;\n\n    MPI_Init(NULL, NULL);\n\n    int world_size;\n    MPI_Comm_size(MPI_COMM_WORLD, &world_size);\n    int world_rank;\n    MPI_Comm_rank(MPI_COMM_WORLD, &world_rank);\n\n    // Initialize local data buffer\n    for (int i = 0; i < n; ++i) {\n        data[i] = 1;\n    }\n\n    // Each process computes its partial sum\n    for (int i = world_rank * (n/world_size); i < (world_rank+1)*(n/world_size); ++i) {\n        partial_sum += data[i];\n    }\n\n    //Reduce all partial sums into total sum at root node\n    MPI_Reduce(&partial_sum, &total_sum, 1, MPI_INT, MPI_SUM, 0, MPI_COMM_WORLD);\n\n    if (world_rank == 0) {\n        printf("Total Sum = %d\n", total_sum);\n    }\n\n    MPI_Finalize();\n}