// Example of setting up a simple HTTPS server with Civetweb\n#include "mongoose.h"\n\nstatic void helloworld(struct mg_connection *nc, int ev, void *p) {\n    if (ev == MG_EV_HTTP_REQUEST) {\n        mg_printf(nc, "HTTP/1.1 200 OK\r\nContent-Type: text/plain\r\nConnection: close\r\n\r\nHello World!");\n    }\n}\n\nint main(void) {\n    struct mg_mgr mgr;\n    struct mg_connection *nc;\n    const char *options[] = {"listening_ports", "8443s", \n                             "ssl_certificate", "./server.pem", NULL};\n    mg_mgr_init(&mgr, NULL);\n    nc = mg_bind_opt(&mgr, options[1], helloworld, *(struct mg_bind_opts*)options+2);\n\n    if (nc == NULL) {\n        fprintf(stderr, "Failed to create listener\n");\n        return 1;\n    }\n\n    printf("Starting HTTPS server on port %s\n", options[1]);\n    for (;;) mg_mgr_poll(&mgr, 1000);\n    mg_mgr_free(&mgr);\n\n    return 0;\n}