// This is a conceptual example and might require linking with Argon2 library.\n#include <stdio.h>\n#include <string.h>\n#include "argon2.h"\n\nint main() {\n    const char* password = "my_secure_password";\n    char encoded[ARGON2_DEFAULT_ENCODEDLEN];\n    \n    if (argon2_encoded(1, 4096, 1,\n                       password, strlen(password),\n                       NULL, 0,\n                       encoded, sizeof(encoded), Argon2_i) != ARGON2_OK) {\n        fprintf(stderr, "Error encoding\n");\n        return -1;\n    }\n    \n    printf("Encoded string: %s\n", encoded);\n    return 0;\n}