// Fragment Shader Code\nconst char* fragment_shader_code = \n"uniform sampler2D textureSampler;"\n"uniform float lowerThreshold;"\n"uniform float upperThreshold;"\n\n"void main(void)"\n"{"\n"    vec4 texelColor = texture2D(textureSampler, gl_TexCoord[0].st);"\n"    float grayScaleValue = dot(texelColor.rgb, vec3(0.299, 0.587, 0.114));" // Convert to grayscale if needed\n"    if (grayScaleValue < lowerThreshold || grayScaleValue > upperThreshold)"\n"        gl_FragColor = vec4(0, 0, 0, texelColor.a);"\n"    else"\n"    {"\n"        float normalizedValue = (grayScaleValue - lowerThreshold) / (upperThreshold - lowerThreshold);"\n"        gl_FragColor = vec4(normalizedValue, normalizedValue, normalizedValue, texelColor.a);"\n"    }"\n"}";