// Ensure proper cleanup\nvoid CleanupServerBinding(RPC_BINDING_HANDLE hBinding) {\n    if (hBinding != NULL) {\n        RpcEpUnregister(\n            IMyInterface_v1_0_s_ifspec,\n            hBinding,\n            NULL);\n        RpcBindingFree(&hBinding);\n    }\n}\n\n// Correct initialization and usage of binding handle\nRPC_STATUS InitializeServer() {\n    RPC_BINDING_HANDLE hBinding = NULL;\n    RPC_STATUS status;\n\n    // Create a protocol sequence endpoint.\n    status = RpcServerUseProtseqEp(\n        (unsigned char *)"ncacn_ip_tcp",\n        1300,\n        (unsigned char *)"8080",\n        NULL);\n\n    if (status) {\n        CleanupServerBinding(hBinding);\n        return status;\n    }\n\n    // Register the service.\n    status = RpcServerRegisterIf(\n        IMyInterface_v1_0_s_ifspec,\n        NULL,\n        NULL);\n\n    if (status) {\n        CleanupServerBinding(hBinding);\n        return status;\n    }\n\n    // Run the server.\n    status = RpcServerListen(1, 20, TRUE);\n\n    if (status) {\n        CleanupServerBinding(hBinding);\n        return status;\n    }\n\n    rpc_mgmt_set_server_idle_timeout(0); // Prevents auto-shutdown\n    return RPC_S_OK;\n}