#define _GNU_SOURCE         /* See feature_test_macros(7) */\n#include <dlfcn.h>\n#include <stdarg.h>\n#include <stdio.h>\n#include <unistd.h>\n#include <sys/types.h>\n#include <time.h>\n\ntypedef ssize_t (*original_write_ptr)(int, const void *, size_t);\n\nssize_t write(int fd, const void *buf, size_t count) {\n    static original_write_ptr orig_write = NULL;\n\n    if (!orig_write) {\n        orig_write = (original_write_ptr)dlsym(RTLD_NEXT, "write");\n        if (!orig_write)\n            return -1;\n    }\n\n    // Check for the specific file descriptor you want to modify\n    if (fd == 42) { // Example FD\n        char prefix[100];\n        time_t now = time(NULL);\n        pid_t pid = getpid();\n        snprintf(prefix, sizeof(prefix), "[%ld - %d] ", now, pid);\n\n        // Write the prefix first\n        orig_write(fd, prefix, strlen(prefix));\n    }\n\n    return orig_write(fd, buf, count);\n}