// Example pseudo-code to illustrate checking DC/AC coefficients post-decompression\nvoid checkCoefficients(int* dctBlock) {\n    int dcCoefficient = dctBlock[0]; // First element is typically the DC coefficient\n    if (dcCoefficient < 0 || dcCoefficient > 255) { // Out-of-range check\n        printf("DC Coefficient out of range\n");\n    }\n    \n    for (int i = 1; i < 64; ++i) { // AC coefficients from index 1 to 63 in an 8x8 block\n        int acCoefficient = dctBlock[i];\n        if (acCoefficient > 127 || acCoefficient < -127) {\n            printf("AC Coefficient at index %d out of expected range\n", i);\n        }\n    }\n}