#define STB_IMAGE_IMPLEMENTATION\n#include "stb_image.h"\n#include <stdio.h>\n#include <stdlib.h>\n\nint main() {\n    int width, height, channels;\n    unsigned char *img = stbi_load("image.jpg", &width, &height, &channels, 3);\n    \n    if (img == NULL) {\n        fprintf(stderr, "Error in loading the image\n");\n        return -1;\n    }\n\n    printf("Loaded image with a width of %dpx, a height of %dpx and %d channels\n", width, height, channels);\n\n    // You can now use 'img' which is an array of your pixels\n    // Save or process the image data here\n\n    stbi_image_free(img);\n    return 0;\n}