// Using fopen and friends\n#include <stdio.h>\n\nvoid using_fopen() {\n    FILE *file = fopen("example.txt", "w");\n    if (file == NULL) {\n        perror("Failed to open file with fopen");\n        return;\n    }\n    fprintf(file, "Hello, world!\n");\n    fclose(file);\n}\n\n// Using open and friends\n#include <fcntl.h>\n#include <unistd.h>\n\nvoid using_open() {\n    int fd = open("example.txt", O_WRONLY | O_CREAT, 0644);\n    if (fd == -1) {\n        perror("Failed to open file with open");\n        return;\n    }\n    write(fd, "Hello, world!\n", 14);\n    close(fd);\n}\n\nint main() {\n    using_fopen();\n    using_open();\n    return 0;\n}