#define _GNU_SOURCE\n#include <dlfcn.h>\n#include <stdio.h>\n\n// Declare a pointer to hold the original socket() function\nint (*original_socket)(int __domain, int __type, int __protocol);\n\nint socket(int __domain, int __type, int __protocol) {\n    // Load the real socket implementation if it's not already loaded\n    if (!original_socket) {\n        original_socket = dlsym(RTLD_NEXT, "socket");\n    }\n    \n    printf("Intercepted socket call\n");  // Debugging output\n    \n    // Call the real socket function\n    return original_socket(__domain, __type, __protocol);\n}