use Zend\Mail\Message;\nuse Zend\Mime\Part as MimePart;\nuse Zend\Mime\Multipart;\nuse Zend\Mail\Transport\Sendmail;\n\n// Create the message object\n$message = new Message();\n$message->setFrom('sender@example.com')\n        ->addTo('recipient@example.com')\n        ->setSubject('Email with Inline Image');\n\n// Create HTML part referencing the image via cid:\n$htmlBodyContent = '<h1>Hello, World!</h1><p>Here is an inline image:</p><img src="cid:image_cid" alt="Inline Image">';\n$htmlPart = new MimePart($htmlBodyContent);\n$htmlPart->setType('text/html');\n\n// Load the image\n$imagePath = __DIR__ . '/path/to/image.png'; // Absolute path to your image\n$imageData = file_get_contents($imagePath);\n$imagePart = new MimePart($imageData);\n$imagePart->disposition = MimePart::DISPOSITION_INLINE;\n$imagePart->encoding    = MimePart::ENCODING_BASE64;\n$imagePart->type        = 'image/png';\n$imagePart->filename    = basename($imagePath);\n$imagePart->id          = 'image_cid'; // Set the Content-ID to reference it in HTML\n\n// Create a multipart message for combining text and image\n$mimeMessage = new Multipart();\n$mimeMessage->addPart($htmlPart);\n$mimeMessage->addPart($imagePart);\n\n// Attach the multipart to the message\n$message->setBody($mimeMessage)\n        ->getHeaders()->addHeaderLine('Content-Type', 'multipart/mixed; boundary="' . $mimeMessage->getMime()->boundary() . '"');\n\n// Send the email using Sendmail (or any other transport you configure)\n$transport = new Sendmail();\n$transport->send($message);