function setCookieInfo($data,$expiry=0)\n{\n   if($data === false)\n   {\n       //remove cookie!\n       $cookie = '';\n       $expiry = -3600; // Set to a time in the past (1 hour ago)\n   }\n   else\n   {\n       $serial = base64_encode(serialize($data));\n       $hash = md5('your_secret_key'); // Replace 'your_secret_key' with an actual secret key\n       $cookie = $hash."---".$serial;\n   }\n\n   $domain = ($_SERVER['HTTP_HOST'] == 'localhost') ? '' : '.'.$_SERVER['HTTP_HOST'];\n\n   // Set cookie and check if successful\n   if (!setcookie('Auth', $cookie, $expiry, '/', $domain)) {\n       error_log('Failed to set cookie');\n   }\n}