function downloadImage($url, $destination) {\n    // Set up stream context with appropriate headers and options\n    $options = [\n        'http' => [\n            'method' => 'GET',\n            'header' => 'User-Agent: PHP'\n        ]\n    ];\n    $context  = stream_context_create($options);\n    \n    // Use file_get_contents with the created stream context\n    try {\n        if (!file_put_contents($destination, file_get_contents($url, false, $context))) {\n            throw new Exception("File could not be saved.");\n        }\n    } catch (Exception $e) {\n        echo 'Error: ' .  $e->getMessage();\n    }\n}\n\n$PIC_URL = "http://example.com/image.jpg";\n$pk = "/path/to/save/image.jpg";\n\ndownloadImage($PIC_URL, $pk);