<?php \n  include_once('dal.php');\n\n  // Get store data, and output it as JSON.\n  function getStoreInformation($storeID) {\n    $sl = new storeLocator();\n    $result = $sl->getStoreData($storeID);\n\n    $arr = [];\n    while ($row = mysql_fetch_assoc($result)) { \n        $arr[] = $row;\n    }\n    \n    echo json_encode($arr);  // Output JSON data\n  }\n\n  if (isset($_POST['sID'])) {\n      getStoreInformation($_POST['sID']);\n  } else {\n      echo json_encode([]); // Return an empty array if sID is not set\n  }\n?>