<?php\n// Assume $mysqli is your database connection\n\n$categoryName = $_POST['category_name'];\n$docuTitle = $_POST['document_title'];\n\n// Prepare and execute the query to get the category ID\n$stmt = $mysqli->prepare("SELECT id FROM categories WHERE name = ?");\n$stmt->bind_param("s", $categoryName);\n$stmt->execute();\n$stmt->bind_result($catID);\n$stmt->fetch();\n$stmt->close();\n\nif ($catID) {\n    // Prepare and execute the query to insert document with category ID\n    $insertStmt = $mysqli->prepare("INSERT INTO documents (title, category_id) VALUES (?, ?)");\n    $insertStmt->bind_param("si", $docuTitle, $catID);\n    \n    if ($insertStmt->execute()) {\n        echo "Document saved successfully.";\n    } else {\n        echo "Error: " . $mysqli->error;\n    }\n    $insertStmt->close();\n} else {\n    echo "Category not found.";\n}\n?>