<?php\n// Database connection\n$host = 'localhost';\n$db   = 'database_name';\n$user = 'username';\n$pass = 'password';\n$charset = 'utf8mb4';\n\n$dsn = "mysql:host=$host;dbname=$db;charset=$charset";\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\n$pdo = new PDO($dsn, $user, $pass, $options);\n\n// Define number of results per page\n$resultsPerPage = 10;\n\n// Get current page from URL, default to 1 if not set\n$pageNumber = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$pageNumber = max(1, $pageNumber); // Ensure it's at least 1\n\n$offset = ($pageNumber - 1) * $resultsPerPage;\n\n// Query to get the results for the current page\n$stmt = $pdo->prepare("SELECT * FROM your_table_name LIMIT :limit OFFSET :offset");\n$stmt->bindParam(':limit', $resultsPerPage, PDO::PARAM_INT);\n$stmt->bindParam(':offset', $offset, PDO::PARAM_INT);\n$stmt->execute();\n$results = $stmt->fetchAll();\n\n// Query to get total number of results\n$totalQuery = $pdo->query("SELECT COUNT(*) as total FROM your_table_name");\n$totalResults = $totalQuery->fetch()['total'];\n\n// Calculate total pages\n$totalPages = ceil($totalResults / $resultsPerPage);\n\n// Render the current page results\nforeach ($results as $row) {\n    echo "Record: " . htmlspecialchars(implode(", ", $row)) . "<br>\n";\n}\n\n// Pagination links\necho '<div class="pagination">';\nif ($pageNumber > 1) {\n    echo '<a href="?page=' . ($pageNumber - 1) . '">Previous</a> ';\n}\n\nfor ($i = max(1, $pageNumber - 2); $i <= min($totalPages, $pageNumber + 2); $i++) {\n    if ($i == $pageNumber) {\n        echo "<strong>$i</strong> ";\n    } else {\n        echo '<a href="?page=' . $i . '">' . $i . '</a> ';\n    }\n}\n\nif ($pageNumber < $totalPages) {\n    echo '<a href="?page=' . ($pageNumber + 1) . '">Next</a>';\n}\necho '</div>';\n?>