<?php\n$serverName = "localhost"; // If on the same machine\n$connectionOptions = array(\n    "Database" => "YourDatabase", // Replace with the name of your MDF database attached to SQL Server\n    "Uid" => "YourUsername",      // Replace with your SQL Server username\n    "PWD" => "YourPassword"       // Replace with your SQL Server password\n);\n\n$conn = sqlsrv_connect($serverName, $connectionOptions);\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Your SQL operations here\n\nsqlsrv_close($conn);\n?>