$timeWindow = 60 * 5; // 5 minutes in seconds\n   $maxPosts = 5;\n\n   $ip = $_SERVER['REMOTE_ADDR'];\n   $query = "SELECT COUNT(*) FROM posts WHERE ip = ? AND created_at > NOW() - INTERVAL $timeWindow SECOND";\n   $stmt = $conn->prepare($query);\n   $stmt->bind_param("s", $ip);\n   $stmt->execute();\n   $stmt->bind_result($count);\n   $stmt->fetch();\n\n   if ($count >= $maxPosts) {\n       die("You are posting too fast. Please try again later.");\n   }