class Form {\n    private $formElements;\n\n    public function __construct() {\n        $this->formElements = [];\n    }\n\n    public function addSelect($name, array $options) {\n        // Start the select element\n        $selectHtml = "<select name='$name'>";\n\n        // Loop through options and create an <option> for each\n        foreach ($options as $value => $text) {\n            $selectHtml .= "<option value='$value'>$text</option>";\n        }\n\n        // Close the select element\n        $selectHtml .= '</select>';\n\n        // Store this select in form elements\n        $this->formElements[] = $selectHtml;\n    }\n\n    public function displayForm() {\n        echo '<form method="post" action="">';\n        foreach ($this->formElements as $element) {\n            echo $element . "<br />\n";\n        }\n        echo '</form>';\n    }\n}\n\n// Usage\n$form = new Form();\n$form->addSelect('country', ['US' => 'United States', 'CA' => 'Canada']);\n$form->displayForm();