$stmt = $pdo->prepare(\n    "SELECT \n         b.id AS post_id,\n         b.title,\n         b.text,\n         u.id AS user_id,\n         u.username,\n         u.email\n     FROM \n         relations r\n     JOIN \n         blog b ON b.user_id = r.following_id OR (b.user_id = :user_id AND r.follower_id = :user_id)\n     JOIN \n         users u ON u.id = b.user_id\n     WHERE \n         r.follower_id = :user_id\n     ORDER BY \n         b.posted DESC"\n);\n\n$stmt->execute([':user_id' => $userId]);\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);