<?php\nfunction decryptOpenSSL($key1, $key2, $key3, $iv, $token) {\n    // Use the first 8 bytes of each key to form a 24-byte key for 3DES\n    $key = substr($key1, 0, 8) . substr($key2, 0, 8) . substr($key3, 0, 8);\n\n    // Decrypt using OpenSSL\n    $decrypted_string = openssl_decrypt(hex2bin($token), 'des-ede3-cbc', $key, OPENSSL_RAW_DATA, hex2bin($iv));\n\n    return trim($decrypted_string);\n}\n\n// Usage\n$key1 = '1234567890123456';\n$key2 = '6543210987654321';\n$key3 = '6549873210456123';\n$ivec = '0102030405060708';\n$token = '2ad82e09e3b4e7c491e4bb9fb7c606e1';\n\necho decryptOpenSSL($key1, $key2, $key3, $ivec, $token);\n?>