// Assuming $pdo is your database connection object\n$query = "SELECT post_date FROM your_table WHERE some_condition";\n$stmt = $pdo->query($query);\n\nif ($stmt) {\n    while ($record = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        if (isset($record['post_date']) && !is_null($record['post_date'])) {\n            echo $record['post_date'];\n        } else {\n            echo 'Date is not available';\n        }\n    }\n} else {\n    echo 'Error in query: ' . print_r($pdo->errorInfo(), true);\n}