function isTrulyEmpty($value) {\n    // Check if the value is null or undefined (in PHP, uninitialized variables are considered null)\n    if ($value === null) {\n        return true;\n    }\n\n    // Check for empty arrays\n    if (is_array($value) && count($value) === 0) {\n        return true;\n    }\n\n    // Check for empty object properties (if the object has no properties, it's considered empty)\n    if (is_object($value)) {\n        $vars = get_object_vars($value);\n        return isTrulyEmpty($vars);\n    }\n\n    // Check for an empty string\n    if ($value === "") {\n        return true;\n    }\n\n    // If none of the above conditions are met, the value is not truly empty\n    return false;\n}