$dsn = 'mysql:host=your_host;dbname=your_db;charset=utf8';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    foreach ($member_id as $index => $id) {\n        $stmt = $pdo->prepare("UPDATE members SET userrole = :userrole WHERE member_id = :member_id");\n        $stmt->bindParam(':userrole', $userrole[$index]);\n        $stmt->bindParam(':member_id', $id);\n        $stmt->execute();\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}