$dbParams = array(\n    'host'     => 'your_host',\n    'username' => 'your_username',\n    'password' => 'your_password',\n    'dbname'   => 'your_dbname',\n    'driver_options' => array(\n        PDO::MYSQL_ATTR_USE_BUFFERED_QUERY => true\n    )\n);\n\n$db = Zend_Db::factory('Pdo_Mysql', $dbParams);\n$db->beginTransaction();\n\ntry {\n    // Call first stored procedure\n    $stmt1 = $db->query("CALL your_stored_procedure1();");\n    $result1 = $stmt1->fetchAll();\n    \n    // Call second stored procedure\n    $stmt2 = $db->query("CALL your_stored_procedure2();");\n    $result2 = $stmt2->fetchAll();\n\n    // Commit transaction if all is good\n    $db->commit();\n} catch (Exception $e) {\n    // Rollback if something went wrong\n    $db->rollback();\n    throw $e;\n}