<?php\n\n// Your array of dead keywords (patterns)\n$deadKeywords = ['keyword1', 'keyword2', 'another_keyword'];\n\n// Escape special characters in each keyword to avoid regex syntax errors\nforeach ($deadKeywords as &$pattern) {\n    $pattern = preg_quote($pattern, '#');\n}\n\n// Compile all patterns into a single pattern separated by '|'\n$combinedPattern = '#(' . implode('|', $deadKeywords) . ')#';\n\n// Example HTML content to check against\n$htmlContent = 'This is some example text containing keyword1 which should match.';\n\n// Perform the regex match\nif (preg_match($combinedPattern, $htmlContent, $matches)) {\n    echo "Match found: " . $matches[0];\n} else {\n    echo "No match found.";\n}\n\n?>