function uploadImage() {\n    if (!empty($this->data['YourModel']['image'])) {\n        $file = $this->data['YourModel']['image'];\n\n        // Check if it's an uploaded file\n        if (is_uploaded_file($file['tmp_name'])) {\n            // Get the dimensions of the image\n            list($width, $height) = getimagesize($file['tmp_name']);\n\n            // Example: Only allow images with a width between 800 and 1600 pixels,\n            // and a height between 450 and 900 pixels.\n            if ($width >= 800 && $width <= 1600 && $height >= 450 && $height <= 900) {\n                // Proceed with saving the file\n                // ...\n            } else {\n                $this->Session->setFlash('Image dimensions must be between 800-1600 pixels width and 450-900 pixels height.');\n                return;\n            }\n        }\n    }\n\n    // Save the model or handle errors\n    if ($this->YourModel->save($this->data)) {\n        $this->Session->setFlash('Image uploaded successfully.');\n    } else {\n        $this->Session->setFlash('Failed to upload image.');\n    }\n}