function convertUnicode($str) {\n    return preg_replace_callback('/%u([0-9a-f]{4})/i', function($matches) {\n        $charCode = hexdec(ltrim($matches[1], '0'));\n        return mb_convert_encoding('&#' . $charCode . ';', "UTF-8", "HTML-ENTITIES");\n    }, $str);\n}\n\n// Usage\n$postData = $_POST['your_parameter_name'];\n$decodedString = convertUnicode($postData);\n\necho htmlspecialchars($decodedString, ENT_QUOTES, 'UTF-8');