// Assuming you have already established a PDO connection: $pdo\n\necho '<td background="images/map/';\n\ntry {\n    // Prepare the SQL statement\n    $stmt = $pdo->prepare("SELECT image FROM map WHERE horizontal = :horizontal AND verticle = :verticle");\n\n    // Bind the parameters\n    $horizontalValue = ${'mapPiece' . $mapPieceCount . '[0]'};\n    $verticleValue = ${'mapPiece' . $mapPieceCount . '[1]'}; // Assuming you meant to access [1] for verticle\n\n    $stmt->bindParam(':horizontal', $horizontalValue, PDO::PARAM_INT);\n    $stmt->bindParam(':verticle', $verticleValue, PDO::PARAM_INT);\n\n    // Execute the statement\n    $stmt->execute();\n\n    // Fetch the result\n    $tile = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    // Output the image name\n    echo htmlspecialchars($tile['image']) . '.png"></td>';\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}