<?php\n// Enable error reporting for debugging\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\nrequire_once('lib/nusoap.php'); \n$wsdl="http://localhost:8080/HelloWorldWS/sayHiService?WSDL";\n$client=new nusoap_client($wsdl, 'wsdl');\n\n// Check for any errors in creating the SOAP client\nif ($client->getError()) {\n    echo "SOAP client creation error: ";\n    print_r($client->getError());\n    exit(1);\n}\n\n$param='John'; # Make sure this matches what your service expects\n\n// Call the remote method and pass parameters\n$result = $client->call('Hey', array('name' => $param));\n\nif ($client->fault) {\n    // Check for a SOAP fault\n    print_r($result);\n} else {\n    $error = $client->getError();\n    if ($error) {\n        // Display the error message\n        echo "Error: ";\n        print_r($error);\n    } else {\n        // Display the result\n        print_r($result);\n    }\n}\n\nunset($client);\n?>